import DetailNavBar from './childComps/DetailNavBar.vue';
import DetailSwiper from './childComps/DetailSwiper.vue';
import DetailBaseInfo from './childComps/DetailBaseInfo.vue';
import DetailShopInfo from './childComps/DetailShopInfo.vue';
import DetailGoodsInfo from './childComps/DetailGoodsInfo.vue';
import DetailParamInfo from './childComps/DetailParamInfo.vue';
import DetailCommentInfo from './childComps/DetailCommentInfo.vue';
import DetailBottomBar from './childComps/DetailBottomBar.vue';
import Scroll from '../../components/common/scroll/Scroll.vue';
import GoodsList from '../../components/content/goods/GoodsList.vue';
import { getDetail, Goods, Shop, GoodsParam, getRecommend } from '../../network/detail.js';
import { debounce } from '../../common/utils';
import { itemListenerMixin } from '../../common/mixin';
export default {
  name: "Detail",
  components: {
    DetailNavBar: DetailNavBar,
    DetailSwiper: DetailSwiper,
    DetailBaseInfo: DetailBaseInfo,
    DetailShopInfo: DetailShopInfo,
    DetailGoodsInfo: DetailGoodsInfo,
    DetailParamInfo: DetailParamInfo,
    DetailCommentInfo: DetailCommentInfo,
    DetailBottomBar: DetailBottomBar,
    Scroll: Scroll,
    GoodsList: GoodsList
  },
  mixins: [itemListenerMixin],
  data: function data() {
    return {
      iid: null,
      topImages: [],
      goods: {},
      shop: {},
      detailInfo: {},
      paramInfo: {},
      commentInfo: {},
      recommends: [],
      themeTopYs: [],
      getThemeTopY: null,
      currentIndex: 0,
      isShowBackTop: false
    };
  },
  created: function created() {
    var _this = this;

    // 1.保存传入的iid
    this.iid = this.$route.params.iid; // console.log(this.iid);
    // 2.根据iid请求数据

    getDetail(this.iid).then(function (res) {
      // 1.获取顶部的图片轮播数据
      var data = res.result;
      _this.topImages = res.result.itemInfo.topImages; // console.log(this.topImages);
      // 2.获取商品信息

      _this.goods = new Goods(data.itemInfo, data.columns, data.shopInfo.services); // console.log(this.goods);
      // 3.创建店铺信息的对象

      _this.shop = new Shop(data.shopInfo); // 4.保存商品的详情数据

      _this.detailInfo = data.detailInfo; // 5.获取参数信息

      _this.paramInfo = new GoodsParam(data.itemParams.info, data.itemParams.rule); // 6.取出评论的信息

      if (data.rate.cRate !== 0) {
        _this.commentInfo = data.rate.list[0];
      }
    }); // 3.请求推荐数据

    getRecommend().then(function (res) {
      // console.log(res);
      _this.recommends = res.data.list;
    }); // 4.给getThemeTopY赋值（对给this.themeTopYs赋值的操作进行防抖）

    this.getThemeTopY = debounce(function () {
      _this.themeTopYs = [];

      _this.themeTopYs.push(0);

      _this.themeTopYs.push(_this.$refs.params.$el.offsetTop);

      _this.themeTopYs.push(_this.$refs.comment.$el.offsetTop);

      _this.themeTopYs.push(_this.$refs.recommend.$el.offsetTop);

      _this.themeTopYs.push(Number.MAX_VALUE); // console.log(this.themeTopYs);

    }, 100);
  },
  methods: {
    imageLoad: function imageLoad() {
      this.$refs.scroll.refresh();
      this.getThemeTopY();
    },
    titleClick: function titleClick(index) {
      this.$refs.scroll.scrollTo(0, -this.themeTopYs[index], 100);
    },
    contentScroll: function contentScroll(position) {
      // 1.获取y值
      var positionY = -position.y; // 2.positionY和主题中值进行对比

      var length = this.themeTopYs.length;

      for (var i = 0; i < length; i++) {
        // 小技巧（加一个很大的数字在比较）
        if (this.currentIndex !== i && positionY >= this.themeTopYs[i] && positionY < this.themeTopYs[i + 1]) {
          this.currentIndex = i;
          this.$refs.nav.currentIndex = this.currentIndex;
        } // 普通做法
        // if(this.currentIndex !== i && ((i < length - 1 && positionY >= this.themeTopYs[i] && positionY < this.themeTopYs[i + 1]) || (i === length -1 && positionY >= this.themeTopYs[i]))) {
        //     this.currentIndex = i
        //     this.$refs.nav.currentIndex = this.currentIndex

      }
    },
    addToCart: function addToCart() {
      // 1.获取购物车需要展示信息
      var product = {};
      product.image = this.topImages[0];
      product.title = this.goods.title;
      product.desc = this.goods.desc;
      product.price = this.goods.newPrice;
      product.iid = this.iid;
      product.realPrice = this.goods.realPrice;
      console.log(product.realPrice); // 2.将商品添加到购物车里
      // this.$store.commit('addCart', product)

      this.$store.dispatch('addCart', product).then(function (res) {
        console.log(res);
      });
    }
  },
  mounted: function mounted() {// console.log('mounted');
  },
  destroyed: function destroyed() {
    this.$bus.$off('itemImgLoad', this.itemImgListener);
  }
};
