import DetailNavBar from './childComps/DetailNavBar.vue';
import DetailSwiper from './childComps/DetailSwiper.vue';
import DetailBaseInfo from './childComps/DetailBaseInfo.vue';
import DetailShopInfo from './childComps/DetailShopInfo.vue';
import DetailGoodsInfo from './childComps/DetailGoodsInfo.vue';
import DetailParamInfo from './childComps/DetailParamInfo.vue';
import DetailCommentInfo from './childComps/DetailCommentInfo.vue';
import Scroll from '../../components/common/scroll/Scroll.vue';
import GoodsList from '../../components/content/goods/GoodsList.vue';
import { getDetail, Goods, Shop, GoodsParam, getRecommend } from '../../network/detail.js';
import { debounce } from '../../common/utils';
import { itemListenerMixin } from '../../common/mixin';
export default {
  name: "Detail",
  components: {
    DetailNavBar: DetailNavBar,
    DetailSwiper: DetailSwiper,
    DetailBaseInfo: DetailBaseInfo,
    DetailShopInfo: DetailShopInfo,
    DetailGoodsInfo: DetailGoodsInfo,
    DetailParamInfo: DetailParamInfo,
    DetailCommentInfo: DetailCommentInfo,
    Scroll: Scroll,
    GoodsList: GoodsList
  },
  mixins: [itemListenerMixin],
  data: function data() {
    return {
      iid: null,
      topImages: [],
      goods: {},
      shop: {},
      detailInfo: {},
      paramInfo: {},
      commentInfo: {},
      recommends: []
    };
  },
  created: function created() {
    var _this = this;

    // 1.保存传入的iid
    this.iid = this.$route.params.iid; // console.log(this.iid);
    // 2.根据iid请求数据

    getDetail(this.iid).then(function (res) {
      // 1.获取顶部的图片轮播数据
      var data = res.result;
      _this.topImages = res.result.itemInfo.topImages; // console.log(this.topImages);
      // 2.获取商品信息

      _this.goods = new Goods(data.itemInfo, data.columns, data.shopInfo.services); // 3.创建店铺信息的对象

      _this.shop = new Shop(data.shopInfo); // 4.保存商品的详情数据

      _this.detailInfo = data.detailInfo; // 5.获取参数信息

      _this.paramInfo = new GoodsParam(data.itemParams.info, data.itemParams.rule); // 6.取出评论的信息

      if (data.rate.cRate !== 0) {
        _this.commentInfo = data.rate.list[0];
      }
    }); // 3.请求推荐数据

    getRecommend().then(function (res) {
      // console.log(res);
      _this.recommends = res.data.list;
    });
  },
  mounted: function mounted() {},
  destroyed: function destroyed() {
    this.$bus.$off('imageLoad', this.itemImgListener);
  }
};
