import Vue from 'vue'
import Vuex from 'vuex'

// 1.安装插件
Vue.ues(Vuex)

// 2.创建Store对象
const store = new Vuex.Store({
    state: {
        carList: []
    },
    mutations: {
        addCart(store, payload) {
            state.carList.push(payload)
        }
    }
})

// 3.挂载Vue实例上
export default store
